mapper.select:
select m.mem_reg_id, m.membership_no, m.membership_start, m.membership_end , f.fitness_id , m.admin_id, f.fitness_name, f.tel_number, f.zipcode, f.address, F.ADDRESS_DETAIL from membership_reg m, fitness f
m.fitness_id=f.fitness_id
and m.admin_id=#{admin_id}
and m.fitness_id=#{fitness_id}
