mapper.update:
UPDATE exercise_list
SET exe_kinds = #{exe_kinds},
	exe_part = #{exe_part},
	exe_name = #{exe_name}
WHERE exe_no = #{exe_no}

mapper.insert:
INSERT INTO exercise_program_basic
(
	exep_no,
	exep_name
)
VALUES
(
	#{exep_no},
	#{exep_name}
)

mapper.update:
UPDATE exercise_program_basic
SET exep_name = #{exep_name}
WHERE exep_no = #{exep_no}

mapper.delete:
DELETE FROM exe_basic_detail
WHERE ebd_no = #{ebd_no}
AND exep_no = #{exep_no}

mapper.update:
UPDATE exercise_program_personal
SET trainer_id = #{trainer_id},
	exep_name = #{exep_name},
	exep_trainer_id = #{exep_trainer_id},
	exep_date = #{exep_date},
	user_id = #{user_id}
WHERE exep_no = #{exep_no}
AND user_id = #{user_id}

mapper.delete:
DELETE FROM exercise_program_personal
WHERE exep_no = #{exep_no}
AND user_id = #{user_id}

mapper.update:
UPDATE exe_personal_detail
SET exep_no = #{exep_no},
	exe_no = #{exe_no},
	epd_set = #{epd_set},
	epd_count = #{epd_count},
	epd_weight = #{epd_weight}
WHERE epd_no = #{epd_no}
AND user_id = #{user_id}

mapper.delete:
DELETE FROM exe_personal_detail
WHERE epd_no = #{epd_no}
AND user_id = #{user_id}

mapper.update:
UPDATE exercise_record
SET exer_end_date = sysdate,
	exer_do_count = #{exer_do_count},
	exer_do_set = #{exer_do_set}
WHERE exep_no = #{exep_no}
AND exe_no = #{exe_no}
AND user_id = #{user_id}
AND to_char(exer_date, 'yyyy-mm-dd') = to_char(sysdate, 'yyyy-mm-dd')

mapper.delete:
DELETE FROM exercise_record
WHERE exer_no = #{exer_no}
AND user_id = #{user_id}

mapper.update:
UPDATE user_weight
SET weight = #{weight},
	check_date = #{check_date}
WHERE weight_no = #{weight_no}
AND user_id = #{user_id}

mapper.delete:
DELETE FROM user_weight
WHERE weight_no = #{weight_no}
AND user_id = #{user_id}
