mapper.select:
SELECT id ,fitness_name ,address ,address_detail ,email ,tel_number FROM (SELECT * FROM (
SELECT ROWNUM AS SEQ ,id ,fitness_name ,address ,address_detail ,email ,tel_number
FROM ( SELECT * FROM FITNESSADMIN where 1=1
AND fitness_name LIKE #{search}
ORDER BY fitness_name ASC )
) where seq >= #{start} ) where ROWNUM <= 10
