mapper.select:
SELECT board_no ,category ,title ,content ,reg_date ,hit FROM ( select * from ( select rownum as seq, board_no, category, title, content, reg_date, hit FROM( SELECT * FROM user_notice_board where 1=1
and title like #{search}
ORDER BY board_no desc )
)WHERE SEQ >= #{start} ) WHERE rownum <= 10

mapper.insert:
INSERT INTO user_notice_board(
							board_no,
							mem_board_no,
							category,
							title,
							content,
							reg_date,
							hit,
							fitness_id 
							)

VALUES	(
		  (
		  	SELECT NVL(MAX(board_no), 0) + 1 from user_notice_board  
		  )
		 	,#{mem_board_no}
		 	,#{category}
		 	,#{title}
		 	,#{content}
		 	,sysdate
		 	,#{hit} 
		 	,#{fitness_id}
		 )

mapper.update:
UPDATE 		user_notice_board 
SET 		title	= #{title},
			content = #{content}

WHERE		board_no = #{board_no}
